mapper.select:
select name from user
where id = #{id}
and password = #{password}

select count(*)
from follower
where follower_user_seq = #{user_seq}
and following_user_seq = #{following_user_seq}

mapper.delete:
delete from follower
where follower_user_seq = #{user_seq}
and following_user_seq = #{following_user_seq}
